package com.bino.mcp.adapter.out.summary.mapper;

import com.bino.mcp.adapter.out.summary.entity.SummaryEntity;
import com.bino.mcp.domain.Summary;
import org.mapstruct.Mapper;
import org.mapstruct.Mapping;

/**
 * MapStruct mapper: SummaryEntity (JPA) <-> Summary (domain).
 */
@Mapper(componentModel = "spring")
public interface SummaryPersistenceMapper {

    /**
     * Converts a Summary (domain) to SummaryEntity (JPA).
     * Id is ignored as it will be generated by the database.
     */
    @Mapping(target = "id", ignore = true)
    SummaryEntity toEntity(Summary domain);

    /**
     * Converts a SummaryEntity (JPA) to Summary (domain).
     */
    Summary toDomain(SummaryEntity entity);
}
